	public static function checkUseForm($shopName, $shopId) {
		return 
		!empty($shopName) 
		&& strpos($shopName,'キレイモ') === false 
		&& strpos($shopName,'ジェイエステティック') === false 
		&& strpos($shopName,'キレイモ') === false 
		&& strpos($shopName,'エステティックTBC') === false 
		&& strpos($shopName,'銀座カラー') === false 
		&& strpos($shopName,'レイジア') === false 
		&& $shopId != 5037 
